package Assignment1;

public class HappyArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int[] a= {2,1};
int sum=0;
while(sum>9) {
for(int i=0;i<=a.length-1;i++) {
	sum+=a[i*i];
	
}}
if(sum==0) {
	System.out.println("happy array");
}
else {
	System.out.println("not happy array");
}

	}

}
